/*** Validation ***/// Idif ($_REQUEST['id']) {  $id = (int) $_REQUEST['id'];}// Nameif ($_REQUEST['name']) {  $name = $_REQUEST['name'];}// locationif ($_REQUEST['location']) {  $location = $_REQUEST['location'];}// Activeif ($_REQUEST['active']) {  $active = $_REQUEST['active'] ? true : false;}// Typeif ($_REQUEST['type'] AND in_array($_REQUEST['type'], array('blume','baum'))) {  $type = $_REQUEST['type'];}if (!$_REQUEST['id'] OR !$_REQUEST['name'] OR !$_REQUEST['location'] OR !$_REQUEST['type']) {  $error[] = 'missing fields';} 