$type = $index->getType('post');$typeMapping = new \Elastica\Type\Mapping();$typeMapping->setType($type);$typeMapping->setParam('index_analyzer', 'indexAnalyzer');$typeMapping->setParam('search_analyzer', 'searchAnalyzer');$typeMapping->setProperties(array(    'id'        => array('type' => 'integer', 'include_in_all' => true),    'timestamp' => array('type' => 'date', 'include_in_all' => true),      'topic'     => array('type' => 'string', 'include_in_all' => true),    'post'      => array('type' => 'string', 'include_in_all' => true),    'tags'      => array('type' => 'string', 'include_in_all' => true),    'user'      => array(      'type'       => 'object',      'properties' => array(        'firstName' => array('type' => 'string', 'include_in_all' => true),        'lastName'  => array('type' => 'string', 'include_in_all' => true)      ),    ),));$typeMapping->send();