$topics = array('apples','pears','peaches','tomatoes','peppers');$tags = array('green fruit','green fruit','red fruit','red vegetable','red vegetable');$postDocuments = array();for ($i = 0; $i < 5; $i++){  $postDocuments[] = new \Elastica\Document($i, array(      'id'        => $i + 1,      'timestamp' => time() + $i,      'topic'     => $topics[$i],      'post'      => 'Here is a post about ' . $topics[$i],      'tags'      => $tags[$i],      'user'      => array(        'firstName' => 'John',        'lastName'  => 'Doe'      )  ));}$type->addDocuments($postDocuments);$type->getIndex()->refresh();